import React from 'react';
import { useParams } from 'react-router-dom';

function MovieDetails({ movies }) {
    const { id } = useParams();
    const movie = movies.find((movie) => movie.id === parseInt(id));

    if (!movie) {
        return <div>Movie not found</div>;
    }

    return (
        <div>
            <h1>{movie.title}</h1>
            <p>ID: {movie.id}</p>
        </div>
    );
}

export default MovieDetails;
